css:
.my-glitch {
  font-size: 1rem;
  font-weight: bold;
  color: red;
  position: relative;
  animation: glitch 1s linear infinite;
}

@keyframes glitch {
  2%, 64% { transform: translate(2px, 0) skew(0deg); }
  4%, 60% { transform: translate(-2px, 0) skew(0deg); }
  62% { transform: translate(0, 0) skew(5deg); }
}

.my-glitch::before,
.my-glitch::after {
  content: attr(data-text);
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: rgb(28,28,30); /* Колір фону вашого .block4 */
  overflow: hidden;
}

.my-glitch::before {
  left: 2px;
  text-shadow: -1px 0 red;
  animation: glitch-anim-1 2s linear infinite reverse;
}

.my-glitch::after {
  left: -2px;
  text-shadow: -1px 0 blue;
  animation: glitch-anim-2 2s linear infinite reverse;
}

@keyframes glitch-anim-1 {
  0% { clip-path: inset(5% 0 90% 0); }
  100% { clip-path: inset(70% 0 5% 0); }
}

@keyframes glitch-anim-2 {
  0% { clip-path: inset(80% 0 10% 0); }
  100% { clip-path: inset(10% 0 85% 0); }
}
